import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Headers from './Headers';
import DeleteButton from './DeleteButton';

const Details = ({pets, setPets, id, remove}) => {

    useEffect(() => {
        axios.get("http://localhost:8000/pets/" + id)
            .then(res => setPets(res.data))
    }, [])

    const { removet } = remove;
    const DeleteProduct = (id) =>{
        axios.delete(`http://localhost:8000/api/product/${id}/delete`)
        .then(res => removePet(id))

    }

    return (
        <div>
            <Headers/>

            {pets.map((pet, idx)=>{
                return( 
                    <div>
                   
                    <button style = {{marginLeft: "30px"}} type="submit" className="btn btn-dark" onClick = {(e)=> {DeleteProduct(pet._id)}} >delete</button>
        
                    </div>
               )
            })}
           
            <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {pets.PetName}</h1>
            <p style = {{textAlign: "center"}}>Pet type: {pets.PetType}</p>
            <p style = {{textAlign: "center"}}>Description: {pets.desc}</p>
            <p style = {{textAlign: "center"}}>Skills: </p>
            <p>skill 1: {pets.skill_1}</p>
            <p>{pets.skill_2}</p>
            <p>{pets.skill_3}</p>
        </div>
      
    )
}

export default Details
