import { Link } from '@reach/router';
import axios from 'axios';
import React, {useState ,useEffect} from 'react';
import DeleteButton from './DeleteButton';
import Headers from './Headers';


const PetPerent = () => {
    const [pets, setPets] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8000/")
        .then(res=> setPets(res.data))
        .catch(err => console.log(err));
    }, [])
 


return(
    <div>
      <div style = {{display : "flex", marginLeft: "350px", marginTop : "30px"}}>
           <h1>Pet Shelter</h1>
           <h6 style = {{marginTop: "15px", marginLeft: "150px"}}><Link to = "/pets/new">add a pet to the shelter</Link></h6>
        
        </div>

        <table className="table" style = {{margin : "auto",marginTop : "50px", width : "500px"}}>
                <tbody>
                    <tr>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Actions</td>
                    </tr>
                {
                    pets.sort((pet, idx)=> (pet.Type > idx.Type) ? 1 : -1).map((pet, idx)=>{
                        return(
                            <tr key={idx}>
                                <td>
                                    <Link to = {`/pets/${pet._id}`} >{pet.PetName}</Link>
                                </td>
                                <td>
                                    {pet.PetType}
                                </td>
                                <td><Link to={`/pets/edit/${pet._id}`}><button style = {{width : "70px"}} type="submit" className="btn btn-primary">Edit</button></Link></td>
                            
                                <td><DeleteButton deleteId={pet._id} callback={deleteHandler}/></td>

                            </tr>
                        )
                    })
                }
                </tbody>
            </table>
                
    </div>
)

}

export default PetPerent;
