import axios from 'axios';
import React, {useState ,useEffect} from 'react';
import DeleteButton from './DeleteButton';
import Headers from './Headers';


const PetPerent = () => {
    const [pets, setPets] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8000/")
        .then(res=> setPets(res.data))
        .catch(err => console.log(err));
    }, [])
 
const deleteHandler = PetId => {
    const tempArr = pets.filter( pet => pet._id !== PetId);
    setPets(tempArr);
}

return(
    <div>
       <Headers/>

        <table className="table" style = {{margin : "auto",marginTop : "50px", width : "500px"}}>
                <tbody>
                    <tr>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Actions</td>
                    </tr>
                {
                    pets.sort((pet, idx)=> (pet.Type > idx.Type) ? 1 : -1).map((pet, idx)=>{
                        return(
                            <tr key={idx}>
                                <td>
                                    {pet.PetName}
                                </td>
                                <td>
                                    {pet.Type}
                                </td>
                                <td><Link to={`/edit/${pet._id}`}><button style = {{width : "70px"}} type="submit" className="btn btn-primary">Edit</button></Link></td>
                            
                                <td><DeleteButton deleteId={pet._id} callback={deleteHandler}/></td>

                            </tr>
                        )
                    })
                }
                </tbody>
            </table>
                
    </div>
)

}

export default PetPerent;
