import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Headers from './Headers';
import DeleteButton from './DeleteButton';

const Details = ({pets, setPets, id}) => {
    
    useEffect(() => {
        axios.get("http://localhost:8000/pets/" + id)
            .then(res => setPets(res.data))
    }, [])

    const deleteHandler = PetId => {
        const tempArr = pets.filter( pet => pet._id !== PetId);
        setPets(tempArr);
    }

    return (
        <div>
            <Headers/>
         
           { pets.map((author, idx)=>{
                        return(
                            <tr key={idx}>
                                <td>
                                    {author.Name}
                                </td>
                            
                                <td><Link to={`/edit/${author._id}`}><button style = {{width : "70px"}} type="submit" className="btn btn-primary">Edit</button></Link>
                                <DeleteButton deleteId={author._id} callback={deleteHandler}/></td>

                            </tr>
                        )
                    })}
                    <DeleteButton deleteId={pets._id} callback={deleteHandler}/>
                        
                     
          
           
            <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {pets.PetName}</h1>
            <p style = {{textAlign: "center"}}>Pet type: {pets.PetType}</p>
            <p style = {{textAlign: "center"}}>Description: {pets.desc}</p>
            <p style = {{textAlign: "center"}}>Skills: </p>
            <p>skill 1: {pets.skill_1}</p>
            <p>{pets.skill_2}</p>
            <p>{pets.skill_3}</p>
        </div>
      
    )
}

export default Details
