import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Headers from './Headers';
import DeleteButton from './DeleteButton';

const Details = props => {
 
    useEffect(() => {
        axios.get("http://localhost:8000/pets/" + props.id)
            .then(res => props(res.data))
    }, [])

    const { remove } = props;
    const DeleteProduct = (id) =>{
        axios.delete(`http://localhost:8000/api/product/${id}/delete`)
        .then(res => remove(id))

    }

    return (
        <div>
            <Headers/>

            <DeleteButton deleteId={props._id} callback={DeleteProduct}/>
           
            <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {props.PetName}</h1>
            <p style = {{textAlign: "center"}}>Pet type: {props.PetType}</p>
            <p style = {{textAlign: "center"}}>Description: {props.desc}</p>
            <p style = {{textAlign: "center"}}>Skills: </p>
            <p>skill 1: {props.skill_1}</p>
            <p>{props.skill_2}</p>
            <p>{props.skill_3}</p>
        </div>
      
    )
}

export default Details
