import axios from 'axios';
import React from 'react';

const DeleteButton = ({deleteId, callback, pets, setPets}) => {

    const clickHandler = e => {
        axios.delete(`http://localhost:8000/pets/delete/${deleteId}`)
            .then( res => {
                callback(deleteId)
            })
    }
    return(
        <button style = {{marginLeft : "30px"}} className="btn btn-danger" onClick={clickHandler}>Adopt{pets.Pet}</button>
    )
}

export default DeleteButton;
