import { Link } from '@reach/router';
import axios from 'axios';
import React, {useState ,useEffect} from 'react';
import DeleteButton from './DeleteButton';
import Headers from './Headers';


const Details = ({pets, setPets}) => {
    

    useEffect(() => {
        axios.get("http://localhost:8000/")
        .then(res=> setPets(res.data))
        .catch(err => console.log(err));
    }, [])
 


return(
    <div>
    

      
                {
                    pets.sort((pet, idx)=> (pet.Type > idx.Type) ? 1 : -1).map((pet, idx)=>{
                        return(
                            <tr key={idx}>
                                <td>
                                   {pet.PetName}
                                </td>
                                <td>
                                    {pet.PetType}
                                </td>
                                <td><Link style = {{marginRight:"15px"}}to={`/pets/edit/${pet._id}`}>Edit</Link>
                            
                                <Link to = {`/pets/${pet._id}`} >Details</Link></td>

                            </tr>
                        )
                    })
                }
          



           
            <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {pets.PetName}</h1>
            <p style = {{textAlign: "center"}}>Pet type: {pets.PetType}</p>
            <p style = {{textAlign: "center"}}>Description: {pets.desc}</p>
            <p style = {{textAlign: "center"}}>Skills: </p>
            <p>skill 1: {pets.skill_1}</p>
            <p>{pets.skill_2}</p>
            <p>{pets.skill_3}</p>
        </div>
      
    )
}

export default Details
