import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Headers from './Headers';
import DeleteButton from './';

const Details = props => {
    const [pet, setPets] = useState({})
    useEffect(() => {
        axios.get("http://localhost:8000/pets/" + props.id)
            .then(res => setPets(res.data))
    }, [])

    const deleteHandler = PetId => {
        const tempArr = pet.filter( pet => pet._id !== PetId);
        setPets(tempArr);
    }

    return (
        <div>
            <Headers/>

            <DeleteButton deleteId={pet._id} callback={deleteHandler}/>
           
            <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {pet.PetName}</h1>
            <p style = {{textAlign: "center"}}>Pet type: {pet.PetType}</p>
            <p style = {{textAlign: "center"}}>Description: {pet.desc}</p>
            <p style = {{textAlign: "center"}}>Skills: </p>
            <p>skill 1: {pet.skill_1}</p>
            <p>{pet.skill_2}</p>
            <p>{pet.skill_3}</p>
        </div>
      
    )
}

export default Details
