import {  navigate } from '@reach/router';
import axios from 'axios';
import React, {useEffect} from 'react';
import Headers from './Headers';
import {useRef} from 'react';

const Details = ({pets, setPets}) => {

let btnRef = useRef();

const onBtnClick = e => {
  if(btnRef.current){
    btnRef.current.setAttribute("disabled", "disabled");
  }
}

<button ref={btnRef} onClick={onBtnClick}>Send</button>

    useEffect(() => {
        axios.get("http://localhost:8000/")
        .then(res=> setPets(res.data))
        .catch(err => console.log(err));
    }, [])
 
    const remove = id =>{
        setPets(pets.filter(product => product._id !== id));
    }
    const DeleteProduct = (id) =>{
        axios.delete(`http://localhost:8000/pets/delete/${id}`)
        .then(res => remove(id))
        navigate('/')

    }

return(
    <div>
    
        <Headers />
      
        {
           pets.map((pet, idx)=>{
            return(
                 <div>
                    <button style = {{marginLeft: "30px"}} type="submit" className="btn btn-dark" onClick = {(e)=> {DeleteProduct(pet._id)}} >Apodt {pet.PetName}</button>
                    <h1 style = {{textAlign: "center", marginTop : "30px"}}>Details about: {pet.PetName}</h1>
                    <p style = {{textAlign: "center"}}>Pet type: {pet.PetType}</p>
                    <p style = {{textAlign: "center"}}>Description: {pet.desc}</p>
                    <p style = {{textAlign: "center"}}>Skills: </p>
                    <p>skill 1: {pet.skill_1}</p>
                    <p>{pet.skill_2}</p>
                    <p>{pet.skill_3}</p>

                    <button ref={btnRef} onClick={onBtnClick}>Send</button>
                </div>
                        )
                    })
                }
          



           
           
        </div>
      
    )
}

export default Details
